mapper.select:
    select a.*,b.img_url from product a,image b where a.product_id=b.product_id and b.main_pic=1 and a.status='N'
		
			and a.product_type=#{type}
		
		order by 
		
			(a.total_quantity-a.remain)
		
		
			a.vip_price
		
		
			a.add_time
		
		${sortType}
	limit #{offset},#{length}
